CREATE TABLE `report_account_financial` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `happen_time` datetime NOT NULL COMMENT '发生时间',
  `account_id` bigint(20) NOT NULL COMMENT '客户id',
  `account_name` varchar(45) NOT NULL COMMENT '客户名称',
  `order_code` varchar(45) DEFAULT NULL COMMENT '订单号',
  `contract_code` varchar(45) DEFAULT NULL COMMENT '合同号',
  `serial_code` varchar(45) DEFAULT NULL COMMENT '银行流水号',
  `sale_amount` decimal(18,6) DEFAULT NULL COMMENT '实际销售金额',
  `purchase_amount` decimal(18,6) DEFAULT NULL COMMENT '实际采购金额',
  `received_amount` decimal(18,6) DEFAULT NULL COMMENT '收款金额',
  `payed_amount` decimal(18,6) DEFAULT NULL COMMENT '付款金额',
  `current_balance` decimal(18,6) DEFAULT NULL COMMENT '当前余额（现金和二结之和）',
  `operate_type` varchar(45) NOT NULL COMMENT '操作类型',
  `remark` varchar(200) NOT NULL COMMENT '摘要',
  `created` datetime DEFAULT CURRENT_TIMESTAMP,
  `created_by` varchar(45) DEFAULT NULL,
  `last_updated` datetime DEFAULT CURRENT_TIMESTAMP,
  `last_updated_by` varchar(45) DEFAULT NULL,
  `modification_number` int(20) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='往来单位账务报表'
;

