  `invoice_data_status` VARCHAR(20) NULL COMMENT '买家开票资料状态1.审核通过 2.待审核 3.资料不全 4。审核失败',
  `invoice_data_decline_reason` VARCHAR(255) NULL COMMENT '买家开票资料审核失败原因',
  `bank_data_status` VARCHAR(20) NULL DEFAULT 'Requested' COMMENT '客户银行账号审核状态:Requested待审核,Insufficient资料不足,Declined审核不通过,Approved审核通过',
  `bank_data_reason` VARCHAR(255) NULL COMMENT '客户银行账号审核失败原因',
  `bank_data_reminded` VARCHAR(20) NULL COMMENT '客户银行账号审核通过是否提醒:No不提醒Yes提醒',
  `created` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `created_by` VARCHAR(45) NOT NULL,
  `last_updated` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `last_updated_by` VARCHAR(45) NOT NULL,
  `modification_number` INT(20) NOT NULL DEFAULT '0',
  `row_id` VARCHAR(45) NULL,
  `parent_row_id` VARCHAR(45) NULL,
  `is_deleted` BIT NOT NULL DEFAULT 0 COMMENT '是否逻辑删除，0未删除，1已删除',
  INDEX `fk_cust_account_ext_cust_account_idx` (`cust_account_id` ASC),
  PRIMARY KEY (`id`)
)
ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `steel_cbms`.`cust_contact` (
