  `modification_number` INT(20) NOT NULL DEFAULT '0',
  `row_id` VARCHAR(45) NULL,
  `parent_row_id` VARCHAR(45) NULL,
  `is_deleted` BIT NOT NULL DEFAULT 0 COMMENT '是否逻辑删除，0未删除，1已删除',
