DELIMITER $$
DROP PROCEDURE IF EXISTS pro_account_data_remove $$
CREATE PROCEDURE pro_account_data_remove()
BEGIN
		DECLARE no_more1 						  INT DEFAULT 0;		/*是否达到记录的末尾控制变量*/
		DECLARE no_more2 						  INT DEFAULT 0;		/*是否达到记录的末尾控制变量*/
		DECLARE p_account_id 			 		BIGINT;    			  /*公司id*/
		DECLARE p_department_id	 		  BIGINT;    			  /*部门id*/
		DECLARE p_account_name 	 			VARCHAR(100);		  /*公司名称*/
		DECLARE p_balance 			 			DECIMAL(18, 6);   /*公司现金余额*/
		DECLARE p_balance_freeze 			DECIMAL(18, 6);   /*公司冻结余额*/
	  DECLARE p_balance_second_settlement 			 			DECIMAL(18, 6);   /*公司二次结算余额*/
		DECLARE p_balance_second_settlement_freeze 			DECIMAL(18, 6);   /*公司二次结算冻结余额*/
		DECLARE p_balance_rebate 			DECIMAL(18, 6);   /*公司折让金额*/
		DECLARE p_parent_count	 		  BIGINT;    			  	
		DECLARE p_count	 		  				BIGINT;   

		
    DECLARE cur_1 CURSOR FOR 
				/*查询客户表*/
				SELECT id, name, balance, balance_freeze, balance_second_settlement, balance_second_settlement_freeze, balance_rebate
				FROM cust_account 
				ORDER BY id ASC;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more1 = 1;
    OPEN cur_1;
    FETCH cur_1 INTO p_account_id, p_account_name, p_balance, p_balance_freeze, p_balance_second_settlement, p_balance_second_settlement_freeze, p_balance_rebate; /*获取第一条记录*/
    WHILE no_more1<>1 do
			
			/*判断是否存在部门*/
			SELECT COUNT(id) INTO p_parent_count FROM cust_account WHERE parent_id = p_account_id;

			/*不存在或存在一个部门*/
			IF p_parent_count < 2 THEN
					
					/*判断是否存在默认部门钢材部*/
					SELECT COUNT(id) INTO p_count FROM cust_account WHERE name = '钢材部' AND parent_id = p_account_id;
					
					/*不存在默认部门钢材部*/
					IF p_count = 0 THEN
						/*插入默认部门钢材部*/
						INSERT INTO cust_account (name, balance, balance_freeze, balance_second_settlement, balance_second_settlement_freeze, balance_rebate, org_id, structure_type, parent_id, created_by, last_updated_by)
						VALUES ('钢材部', p_balance, p_balance_freeze, p_balance_second_settlement, p_balance_second_settlement_freeze, p_balance_rebate, 0, 'Department', p_account_id, 'system', 'system');
			
						/*查询部门新增的自增长id*/
						SELECT LAST_INSERT_ID() INTO p_department_id;
					ELSE
						SELECT id INTO p_department_id FROM cust_account WHERE name = '钢材部' AND parent_id = p_account_id;
					END IF;

					/*更新公司金额字段*/
					UPDATE cust_account 
					SET balance = balance - p_balance,
							balance_freeze = balance_freeze - p_balance_freeze,
							balance_second_settlement = balance_second_settlement - p_balance_second_settlement,
							balance_second_settlement_freeze = balance_second_settlement_freeze - p_balance_second_settlement_freeze,
							balance_rebate = balance_rebate - p_balance_rebate
					WHERE id = p_account_id;

					/*插入公司流水*/
					/*
					INSERT INTO cust_account_trans_log (account_id, applyer_id, applyer_name, serial_time, association_type, consign_order_code, apply_type,
					cash_happen_balance, cash_current_balance, amount, current_balance, credit, credit_balance, pay_type, created_by, last_updated_by)
					VALUES (p_account_id, '1', '系统', SYSDATE(), '8', DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'), '1', 
					-p_balance, 0, -p_balance_second_settlement, 0, 0, 0, 'balance', 'system', 'system');
					*/

					/*插入默认部门钢材部流水*/
					/*
					INSERT INTO cust_account_trans_log (account_id, applyer_id, applyer_name, serial_time, association_type, consign_order_code, apply_type,
					cash_happen_balance, cash_current_balance, amount, current_balance, credit, credit_balance, pay_type, created_by, last_updated_by)
					VALUES (p_department_id, '1', '系统', SYSDATE(), '8', DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'), '25', 
					p_balance, p_balance, p_balance_second_settlement, p_balance_second_settlement, 0, 0, 'balance', 'system', 'system');
					*/
				
			END IF;

    FETCH cur_1 INTO p_account_id, p_account_name, p_balance, p_balance_freeze, p_balance_second_settlement, p_balance_second_settlement_freeze, p_balance_rebate; /*取下一条记录*/
    END WHILE;
    CLOSE cur_1;

END $$
DELIMITER ;
call pro_account_data_remove();

