modify by zhoucai@prcsteel.com 修改为按照部门查询
************************************ */
DELIMITER $$
DROP PROCEDURE IF EXISTS pro_account_trans_log_remove $$
CREATE PROCEDURE pro_account_trans_log_remove()
BEGIN
		DECLARE no_more1 						    INT DEFAULT 0;		/*是否达到记录的末尾控制变量*/
		DECLARE no_more2 						  	INT DEFAULT 0;		/*是否达到记录的末尾控制变量*/
		DECLARE p_log_id 			 					BIGINT;    			  /*流水id*/
		DECLARE p_account_id 			 			BIGINT;    			  /*公司id*/
		DECLARE p_department_id	 		  	BIGINT;    			  /*部门id*/
		DECLARE p_account_name 	 				VARCHAR(100);		  /*公司名称*/
		DECLARE p_applyer_id 			 			BIGINT;    			  /*申请人ID*/
		DECLARE p_applyer_name	 		  	VARCHAR(100);     /*申请人姓名*/
		DECLARE p_serial_time 			 		DATETIME;    		  /*流水时间*/
		DECLARE p_association_type	 		VARCHAR(100);    	/*关联类型：１付款单号２寄售交易单号３银行流水号4 还款流水号5 承兑汇票号*/
		DECLARE p_consign_order_code 		VARCHAR(100);    	/*寄售单号*/
		DECLARE p_apply_type	 		  		VARCHAR(100);     /*申请类型*/
		DECLARE p_cash_happen_balance   DECIMAL(18, 6);   /*账户资金发生额*/
		DECLARE p_cash_current_balance 	DECIMAL(18, 6);   /*账户资金当前余额*/
		DECLARE p_amount 			 					DECIMAL(18, 6);   /*二次结算余额发生额*/
		DECLARE p_current_balance 			DECIMAL(18, 6);   /*二次结算当前余额*/
		DECLARE p_credit 			 					DECIMAL(18, 6);   /*额度资金发生额*/
		DECLARE p_credit_balance 		  	DECIMAL(18, 6);   /*额度资金当前余额*/
		DECLARE p_pay_type 			 				DECIMAL(18, 6);   /*支付类型1.余额(balance)2.结算(settlement)3.冻结(freeze)4.预付款(advance_payment)*/  			  	
		
    DECLARE cur_1 CURSOR FOR 
				/*查询客户账户流水表*/
				/*SELECT id, account_id, applyer_id, applyer_name, serial_time, association_type, consign_order_code, apply_type, cash_happen_balance, cash_current_balance, amount, current_balance, credit, credit_balance, pay_type
				FROM cust_account_trans_log 
				ORDER BY id ASC;*/
				SELECT t1.id,	t1.account_id,	t2.id AS depart_id,	t1.applyer_id,	t1.applyer_name,t1.serial_time,
				t1.association_type,t1.consign_order_code,t1.apply_type,t1.cash_happen_balance,t1.cash_current_balance,
				t1.amount,t1.current_balance,t1.credit,	t1.credit_balance,	t1.pay_type
				FROM
					cust_account_trans_log t1
				  JOIN
					cust_account t2 on t2.parent_id=t1.account_id AND t2.`name`='钢材部'
				ORDER BY
				id ASC;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET no_more1 = 1;
    OPEN cur_1;
    FETCH cur_1 INTO p_log_id, p_account_id,p_department_id, p_applyer_id, p_applyer_name, p_serial_time, p_association_type, p_consign_order_code, p_apply_type, p_cash_happen_balance, p_cash_current_balance, p_amount, p_current_balance, p_credit, p_credit_balance, p_pay_type; /*获取第一条记录*/
    WHILE no_more1<>1 do			

/*查询部门id*/
			/*SELECT id INTO p_department_id FROM cust_account WHERE name = '钢材部' AND parent_id = p_account_id;*/
			
			/*如果是银行充值，则分配到部门*/
			IF p_apply_type = 1 THEN
				/*插入公司流水*/
				INSERT INTO cust_account_trans_log (account_id, applyer_id, applyer_name, serial_time, association_type, consign_order_code, apply_type,
				cash_happen_balance, cash_current_balance, amount, current_balance, credit, credit_balance, pay_type, created_by, last_updated_by, created, last_updated)
				VALUES (p_account_id, p_applyer_id, p_applyer_name, DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), '8', DATE_FORMAT(DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), '%Y%m%d%H%i%s'), '1', 
				-p_cash_happen_balance, -p_cash_current_balance, -p_amount, -p_current_balance, 0, 0, 'balance', 'system', 'system', DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), DATE_ADD(p_serial_time,  INTERVAL 1 SECOND));

				/*插入默认部门钢材部流水*/
				INSERT INTO cust_account_trans_log (account_id, applyer_id, applyer_name, serial_time, association_type, consign_order_code, apply_type,
				cash_happen_balance, cash_current_balance, amount, current_balance, credit, credit_balance, pay_type, created_by, last_updated_by, created, last_updated)
				VALUES (p_department_id, p_applyer_id, p_applyer_name, DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), '8', DATE_FORMAT(DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), '%Y%m%d%H%i%s'), '25', 
				p_cash_happen_balance, p_cash_current_balance, p_amount, p_current_balance, 0, 0, 'balance', 'system', 'system', DATE_ADD(p_serial_time,  INTERVAL 1 SECOND), DATE_ADD(p_serial_time,  INTERVAL 1 SECOND));

			ELSE
				/*将原先公司id替换成部门id*/
				UPDATE cust_account_trans_log SET account_id = p_department_id WHERE id = p_log_id;

			END IF;
		
    FETCH cur_1 INTO p_log_id, p_account_id, p_department_id,p_applyer_id, p_applyer_name, p_serial_time, p_association_type, p_consign_order_code, p_apply_type, p_cash_happen_balance, p_cash_current_balance, p_amount, p_current_balance, p_credit, p_credit_balance, p_pay_type; /*取下一条记录*/
    END WHILE;
    CLOSE cur_1;

END $$
DELIMITER ;
call pro_account_trans_log_remove();

