SELECT t3.id,t3.name,t4.reg_time as regTime,t6.nsort_name as nsortName,t3.account_tag as accountTag,t5.name as contact, t5.tel as contactTel,t6.factory as proxyFactory FROM cust_account_contact t1 INNER JOIN cust_account t2 ON t1.account_id=t2.id AND t2.is_deleted=0 INNER JOIN cust_account t3 ON t2.parent_id=t3.id AND t3.is_deleted=0 INNER JOIN cust_account_ext t4 ON t3.id=t4.cust_account_id AND t4.is_deleted=0 LEFT JOIN cust_contact t5 ON t1.contact_id=t5.id AND t5.is_deleted=0 LEFT JOIN cust_proxy_factory t6 on t3.id=t6.account_id AND t6.is_deleted=0 WHERE t1.is_deleted=0
