mapper.select:
  select
  ,
  (select name from base_organization where id = org_id) AS org_name
  from inv_invoice_out_checklist
  where 1=1

		AND org_id IN

 			#{id}



  	and status = #{status,jdbcType=VARCHAR}

  order by created desc
  limit #{start,jdbcType=INTEGER},#{length,jdbcType=INTEGER }

  select count(1)
  from inv_invoice_out_checklist
  where 1=1

		AND org_id IN

 			#{id}



    and status = #{status,jdbcType=VARCHAR}
